// Minimal GSAP-compatible wrapper for basic animations
const gsap = {
  to: function(target, duration, vars) {
    const element = typeof target === 'string' ? document.querySelector(target) : target;
    if (!element) return;
    
    const delay = vars.delay || 0;
    const properties = {...vars};
    delete properties.delay;
    
    setTimeout(() => {
      if (properties.display !== undefined) {
        element.style.display = properties.display;
      }
      if (properties.opacity !== undefined) {
        element.style.transition = `opacity ${duration}s ease`;
        element.style.opacity = properties.opacity;
      }
      if (properties.y !== undefined) {
        element.style.transition = `transform ${duration}s ease`;
        element.style.transform = `translateY(${properties.y})`;
      }
    }, delay * 1000);
  }
};
